/**
 * New node file
 */
var log = require('logger')(module);
	
exports.initial = function(router){
	var response = router.response;
	response.writeHead({"Content-Type":"text/plane"});
	if(router.segment(1)) {
		log(router.segment(1));
		response.write(router.segment(1));
	} else {
		log('article');
		response.write('article');
	}

	response.end();
}
