/**
 * New node file
 */

exports.initial = function(request,response){
	var url = require('url'),
		getFullPAth = function(){
			return url.parse(request.url).pathname.substring(1)||"";
		},
		segments = function(){
			var gfp = getFullPAth();
			return (gfp!="")?gfp.split('/'):[] 
		}();
	
	return {
		request:request,
		response:response,
		pathname:getFullPAth,
		segment:function(i){
			if(segments[i])
				return segments[i];
		}
	};
};
